<BODY ONLOAD="if(window.focus) window.focus()">
<H2>Index</H2>
<H4 CLASS="st">Informations</H4>Author: <A HREF="&#109;ailto:claude_minette&#64;hotmail.com?subject=Index">Min's</A><BR>License: Freeware
<H4 CLASS="st">Description</H4>This class prints an index from the created bookmarks. It therefore requires the
<A HREF="http://www.fpdf.org/?go=script&id=1" TARGET="_blank">bookmarks</A> extension.
<H4 CLASS="st">Source</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br><font class="kw">require(</font><font class="str">'bookmark.php'</font><font class="kw">);<br><br>class&nbsp;</font>PDF_Index&nbsp;<font class="kw">extends&nbsp;</font>PDF_Bookmark<br><font class="kw">{<br>function&nbsp;</font>CreateIndex<font class="kw">(){<br>&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//Index title<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>SetFontSize<font class="kw">(</font>20<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>5<font class="kw">,</font><font class="str">'Index'</font><font class="kw">,</font>0<font class="kw">,</font>1<font class="kw">,</font><font class="str">'C'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>SetFontSize<font class="kw">(</font>15<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Ln<font class="kw">(</font>10<font class="kw">);<br><br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$size<font class="kw">=</font>sizeof<font class="kw">(</font>$<font class="kw">this-&gt;</font>outlines<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$PageCellSize<font class="kw">=</font>$<font class="kw">this-&gt;</font>GetStringWidth<font class="kw">(</font><font class="str">'p. '</font><font class="kw">.</font>$<font class="kw">this-&gt;</font>outlines<font class="kw">[</font>$size<font class="kw">-</font>1<font class="kw">][</font><font class="str">'p'</font><font class="kw">])+</font>2<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;for&nbsp;(</font>$i<font class="kw">=</font>0<font class="kw">;</font>$i<font class="kw">&lt;</font>$size<font class="kw">;</font>$i<font class="kw">++){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//Offset<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$level<font class="kw">=</font>$<font class="kw">this-&gt;</font>outlines<font class="kw">[</font>$i<font class="kw">][</font><font class="str">'l'</font><font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$level<font class="kw">&gt;</font>0<font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>$level<font class="kw">*</font>8<font class="kw">);<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//Caption<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$str<font class="kw">=</font>$<font class="kw">this-&gt;</font>outlines<font class="kw">[</font>$i<font class="kw">][</font><font class="str">'t'</font><font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$strsize<font class="kw">=</font>$<font class="kw">this-&gt;</font>GetStringWidth<font class="kw">(</font>$str<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$avail_size<font class="kw">=</font>$<font class="kw">this-&gt;</font>w<font class="kw">-</font>$<font class="kw">this-&gt;</font>lMargin<font class="kw">-</font>$<font class="kw">this-&gt;</font>rMargin<font class="kw">-</font>$PageCellSize<font class="kw">-(</font>$level<font class="kw">*</font>8<font class="kw">)-</font>4<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;while&nbsp;(</font>$strsize<font class="kw">&gt;=</font>$avail_size<font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$str<font class="kw">=</font>substr<font class="kw">(</font>$str<font class="kw">,</font>0<font class="kw">,-</font>1<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$strsize<font class="kw">=</font>$<font class="kw">this-&gt;</font>GetStringWidth<font class="kw">(</font>$str<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>$strsize<font class="kw">+</font>2<font class="kw">,</font>$<font class="kw">this-&gt;</font>FontSize<font class="kw">+</font>2<font class="kw">,</font>$str<font class="kw">);<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//Filling dots<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$w<font class="kw">=</font>$<font class="kw">this-&gt;</font>w<font class="kw">-</font>$<font class="kw">this-&gt;</font>lMargin<font class="kw">-</font>$<font class="kw">this-&gt;</font>rMargin<font class="kw">-</font>$PageCellSize<font class="kw">-(</font>$level<font class="kw">*</font>8<font class="kw">)-(</font>$strsize<font class="kw">+</font>2<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$nb<font class="kw">=</font>$w<font class="kw">/</font>$<font class="kw">this-&gt;</font>GetStringWidth<font class="kw">(</font><font class="str">'.'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$dots<font class="kw">=</font>str_repeat<font class="kw">(</font><font class="str">'.'</font><font class="kw">,</font>$nb<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>$w<font class="kw">,</font>$<font class="kw">this-&gt;</font>FontSize<font class="kw">+</font>2<font class="kw">,</font>$dots<font class="kw">,</font>0<font class="kw">,</font>0<font class="kw">,</font><font class="str">'R'</font><font class="kw">);<br><br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//Page number<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>$PageCellSize<font class="kw">,</font>$<font class="kw">this-&gt;</font>FontSize<font class="kw">+</font>2<font class="kw">,</font><font class="str">'p. '</font><font class="kw">.</font>$<font class="kw">this-&gt;</font>outlines<font class="kw">[</font>$i<font class="kw">][</font><font class="str">'p'</font><font class="kw">],</font>0<font class="kw">,</font>1<font class="kw">,</font><font class="str">'R'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>}<br>}<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>

<H4 CLASS="st">Example</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br>define<font class="kw">(</font><font class="str">'FPDF_FONTPATH'</font><font class="kw">,</font><font class="str">'font/'</font><font class="kw">);<br>require(</font><font class="str">'createindex.php'</font><font class="kw">);<br><br></font>$pdf<font class="kw">=new&nbsp;</font>PDF_Index<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>Open<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">''</font><font class="kw">,</font>15<font class="kw">);<br><br></font><font class="cmt">//Page 1<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Section 1'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>6<font class="kw">,</font><font class="str">'Section 1'</font><font class="kw">,</font>0<font class="kw">,</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Subsection 1'</font><font class="kw">,</font>1<font class="kw">,-</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>6<font class="kw">,</font><font class="str">'Subsection 1'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Ln<font class="kw">(</font>50<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Subsection 2'</font><font class="kw">,</font>1<font class="kw">,-</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>6<font class="kw">,</font><font class="str">'Subsection 2'</font><font class="kw">);<br><br></font><font class="cmt">//Page 2<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Section 2'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>6<font class="kw">,</font><font class="str">'Section 2'</font><font class="kw">,</font>0<font class="kw">,</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Subsection 1'</font><font class="kw">,</font>1<font class="kw">,-</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>6<font class="kw">,</font><font class="str">'Subsection 1'</font><font class="kw">);<br><br></font><font class="cmt">//Index<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>Bookmark<font class="kw">(</font><font class="str">'Index'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>CreateIndex<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>Output<font class="kw">();<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>
