ontt1.php, addfontttf.php&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/<br>/* other necessary software: pfm2afm, ttf2pt1, fpdf&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/<br>/* For more information, see readme.txt&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/<br>/*&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/<br>/* This file processes TrueType fonts&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;*/<br>/******************************************************************************/<br><br></font><font class="kw">require(</font><font class="str">'makefont.php'</font><font class="kw">);<br><br>function </font>EncodingList<font class="kw">()<br>{<br>&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">// list all available encodings<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$d<font class="kw">=</font>dir<font class="kw">(</font><font class="str">'.'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;while(</font>$f<font class="kw">=</font>$d<font class="kw">-&gt;</font>read<font class="kw">())<br>&nbsp;&nbsp;&nbsp;&nbsp;{<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>preg_match<font class="kw">(</font><font class="str">'/([a-z0-9_-]+)\\.map$/i'</font><font class="kw">,</font>$f<font class="kw">,</font>$res<font class="kw">))<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$enc<font class="kw">[]=</font>$res<font class="kw">[</font>1<font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$d<font class="kw">-&gt;</font>close<font class="kw">();<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>sort<font class="kw">(</font>$enc<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;echo </font><font class="str">'&lt;SELECT NAME="enc"&gt;'</font><font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;foreach(</font>$enc <font class="kw">as </font>$e<font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>printf<font class="kw">(</font><font class="str">'&lt;OPTION %s&gt;%s&lt;/OPTION&gt;'</font><font class="kw">,</font>$e<font class="kw">==</font><font class="str">'cp1252' </font><font class="kw">? </font><font class="str">'SELECTED'</font><font class="kw