<NOBR><code><font color="#000000">
&lt;?php<br><font class="cmt">/*<br>Version 2<br>Changes since version 1:<br>- added function MirrorP()<br>- added function MirrorL()<br>- fixed bug in Translate(): the movement is now performed in user units instead of pts<br>*/<br></font><font class="kw">require(</font><font class="str">'fpdf.php'</font><font class="kw">);<br><br>class </font>PDF_Transform <font class="kw">extends </font>FPDF<font class="kw">{<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>StartTransform<font class="kw">(){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//save the current graphic state<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>_out<font class="kw">(</font><font class="str">'q'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>ScaleX<font class="kw">(</font>$s_x<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(</font>$s_x<font class="kw">, </font>100<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>ScaleY<font class="kw">(</font>$s_y<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(</font>100<font class="kw">, </font>$s_y<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>ScaleXY<font class="kw">(</font>$s<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(</font>$s<font class="kw">, </font>$s<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>Scale<font class="kw">(</font>$s_x<font class="kw">, </font>$s_y<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$x <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">=</font>$<font class="kw">this-&gt;</font>x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$y <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=</font>$<font class="kw">this-&gt;</font>y<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$s_x <font class="kw">== </font>0 <font class="kw">|| </font>$s_y <font class="kw">== </font>0<font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Error<font class="kw">(</font><font class="str">'Please use values unequal to zero for Scaling'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=(</font>$<font class="kw">this-&gt;</font>h<font class="kw">-</font>$y<font class="kw">)*</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">*=</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//calculate elements of transformation matrix<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$s_x<font class="kw">/=</font>100<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$s_y<font class="kw">/=</font>100<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>0<font class="kw">]=</font>$s_x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>1<font class="kw">]=</font>0<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>2<font class="kw">]=</font>0<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>3<font class="kw">]=</font>$s_y<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>4<font class="kw">]=</font>$x<font class="kw">*(</font>1<font class="kw">-</font>$s_x<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>5<font class="kw">]=</font>$y<font class="kw">*(</font>1<font class="kw">-</font>$s_y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//scale the coordinate system<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Transform<font class="kw">(</font>$tm<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>MirrorH<font class="kw">(</font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(-</font>100<font class="kw">, </font>100<font class="kw">, </font>$x<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>MirrorV<font class="kw">(</font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(</font>100<font class="kw">, -</font>100<font class="kw">, </font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>MirrorP<font class="kw">(</font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">,</font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(-</font>100<font class="kw">, -</font>100<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>MirrorL<font class="kw">(</font>$angle<font class="kw">=</font>0<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">,</font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Scale<font class="kw">(-</font>100<font class="kw">, </font>100<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Rotate<font class="kw">(-</font>2<font class="kw">*(</font>$angle<font class="kw">-</font>90<font class="kw">),</font>$x<font class="kw">,</font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>TranslateX<font class="kw">(</font>$t_x<font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Translate<font class="kw">(</font>$t_x<font class="kw">, </font>0<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>TranslateY<font class="kw">(</font>$t_y<font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Translate<font class="kw">(</font>0<font class="kw">, </font>$t_y<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>Translate<font class="kw">(</font>$t_x<font class="kw">, </font>$t_y<font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//calculate elements of transformation matrix<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>0<font class="kw">]=</font>1<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>1<font class="kw">]=</font>0<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>2<font class="kw">]=</font>0<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>3<font class="kw">]=</font>1<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>4<font class="kw">]=</font>$t_x<font class="kw">*</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>5<font class="kw">]=-</font>$t_y<font class="kw">*</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//translate the coordinate system<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Transform<font class="kw">(</font>$tm<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>Rotate<font class="kw">(</font>$angle<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$x <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">=</font>$<font class="kw">this-&gt;</font>x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$y <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=</font>$<font class="kw">this-&gt;</font>y<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=(</font>$<font class="kw">this-&gt;</font>h<font class="kw">-</font>$y<font class="kw">)*</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">*=</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//calculate elements of transformation matrix<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>0<font class="kw">]=</font>cos<font class="kw">(</font>deg2rad<font class="kw">(</font>$angle<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>1<font class="kw">]=</font>sin<font class="kw">(</font>deg2rad<font class="kw">(</font>$angle<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>2<font class="kw">]=-</font>$tm<font class="kw">[</font>1<font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>3<font class="kw">]=</font>$tm<font class="kw">[</font>0<font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>4<font class="kw">]=</font>$x<font class="kw">+</font>$tm<font class="kw">[</font>1<font class="kw">]*</font>$y<font class="kw">-</font>$tm<font class="kw">[</font>0<font class="kw">]*</font>$x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>5<font class="kw">]=</font>$y<font class="kw">-</font>$tm<font class="kw">[</font>0<font class="kw">]*</font>$y<font class="kw">-</font>$tm<font class="kw">[</font>1<font class="kw">]*</font>$x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//rotate the coordinate system around ($x,$y)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Transform<font class="kw">(</font>$tm<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>SkewX<font class="kw">(</font>$angle_x<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Skew<font class="kw">(</font>$angle_x<font class="kw">, </font>0<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>SkewY<font class="kw">(</font>$angle_y<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Skew<font class="kw">(</font>0<font class="kw">, </font>$angle_y<font class="kw">, </font>$x<font class="kw">, </font>$y<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>Skew<font class="kw">(</font>$angle_x<font class="kw">, </font>$angle_y<font class="kw">, </font>$x<font class="kw">=</font><font class="str">''</font><font class="kw">, </font>$y<font class="kw">=</font><font class="str">''</font><font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$x <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">=</font>$<font class="kw">this-&gt;</font>x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$y <font class="kw">=== </font><font class="str">''</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=</font>$<font class="kw">this-&gt;</font>y<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$angle_x <font class="kw">&lt;= -</font>90 <font class="kw">|| </font>$angle_x <font class="kw">&gt;= </font>90 <font class="kw">|| </font>$angle_y <font class="kw">&lt;= -</font>90 <font class="kw">|| </font>$angle_y <font class="kw">&gt;= </font>90<font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Error<font class="kw">(</font><font class="str">'Please use values between -90° and 90° for skewing'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$x<font class="kw">*=</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$y<font class="kw">=(</font>$<font class="kw">this-&gt;</font>h<font class="kw">-</font>$y<font class="kw">)*</font>$<font class="kw">this-&gt;</font>k<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//calculate elements of transformation matrix<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>0<font class="kw">]=</font>1<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>1<font class="kw">]=</font>tan<font class="kw">(</font>deg2rad<font class="kw">(</font>$angle_y<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>2<font class="kw">]=</font>tan<font class="kw">(</font>deg2rad<font class="kw">(</font>$angle_x<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>3<font class="kw">]=</font>1<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>4<font class="kw">]=-</font>$tm<font class="kw">[</font>2<font class="kw">]*</font>$y<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$tm<font class="kw">[</font>5<font class="kw">]=-</font>$tm<font class="kw">[</font>1<font class="kw">]*</font>$x<font class="kw">;<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//skew the coordinate system<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Transform<font class="kw">(</font>$tm<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>Transform<font class="kw">(</font>$tm<font class="kw">){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>_out<font class="kw">(</font>sprintf<font class="kw">(</font><font class="str">'%.3f %.3f %.3f %.3f %.3f %.3f cm'</font><font class="kw">, </font>$tm<font class="kw">[</font>0<font class="kw">],</font>$tm<font class="kw">[</font>1<font class="kw">],</font>$tm<font class="kw">[</font>2<font class="kw">],</font>$tm<font class="kw">[</font>3<font class="kw">],</font>$tm<font class="kw">[</font>4<font class="kw">],</font>$tm<font class="kw">[</font>5<font class="kw">]));<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br><br>&nbsp;&nbsp;&nbsp;&nbsp;function </font>StopTransform<font class="kw">(){<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="cmt">//restore previous graphic state<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>_out<font class="kw">(</font><font class="str">'Q'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>}<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>

<H4 CLASS="st">Example</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br><font class="kw">require(</font><font class="str">'transform.php'</font><font class="kw">);<br><br></font>$pdf <font class="kw">= new </font>PDF_Transform<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">''</font><font class="kw">,</font>12<font class="kw">);<br><br></font><font class="cmt">//Scaling<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>20<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>19<font class="kw">, </font><font class="str">'Scale'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//Scale by 150% centered by (50,30) which is the lower left corner of the rectangle<br></font>$pdf<font class="kw">-&gt;</font>ScaleXY<font class="kw">(</font>150<font class="kw">, </font>50<font class="kw">, </font>30<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>20<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>19<font class="kw">, </font><font class="str">'Scale'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Translation<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>20<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>19<font class="kw">, </font><font class="str">'Translate'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//Translate 7 to the right, 5 to the bottom<br></font>$pdf<font class="kw">-&gt;</font>Translate<font class="kw">(</font>7<font class="kw">, </font>5<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>20<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>19<font class="kw">, </font><font class="str">'Translate'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Rotation<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>50<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>49<font class="kw">, </font><font class="str">'Rotate'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//Rotate 20 degrees counter-clockwise centered by (50,60) which is the lower left corner of the rectangle<br></font>$pdf<font class="kw">-&gt;</font>Rotate<font class="kw">(</font>20<font class="kw">, </font>50<font class="kw">, </font>60<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>50<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>49<font class="kw">, </font><font class="str">'Rotate'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Skewing<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>50<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>49<font class="kw">, </font><font class="str">'Skew'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//skew 30 degrees along the x-axis centered by (125,60) which is the lower left corner of the rectangle<br></font>$pdf<font class="kw">-&gt;</font>SkewX<font class="kw">(</font>30<font class="kw">, </font>125<font class="kw">, </font>60<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>50<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>49<font class="kw">, </font><font class="str">'Skew'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Mirroring horizontally<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>80<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>79<font class="kw">, </font><font class="str">'MirrorH'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//mirror horizontally with axis of reflection at x-position 50 (left side of the rectangle)<br></font>$pdf<font class="kw">-&gt;</font>MirrorH<font class="kw">(</font>50<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>80<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>79<font class="kw">, </font><font class="str">'MirrorH'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Mirroring vertically<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>80<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>79<font class="kw">, </font><font class="str">'MirrorV'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//mirror vertically with axis of reflection at y-position 90 (bottom side of the rectangle)<br></font>$pdf<font class="kw">-&gt;</font>MirrorV<font class="kw">(</font>90<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>80<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>79<font class="kw">, </font><font class="str">'MirrorV'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Point reflection<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>110<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>109<font class="kw">, </font><font class="str">'MirrorP'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//point reflection at the lower left point of rectangle<br></font>$pdf<font class="kw">-&gt;</font>MirrorP<font class="kw">(</font>50<font class="kw">,</font>120<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>50<font class="kw">, </font>110<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>50<font class="kw">, </font>109<font class="kw">, </font><font class="str">'MirrorP'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font><font class="cmt">//Mirroring against a straigth line described by a point (120, 120) and an angle -20°<br></font>$angle<font class="kw">=-</font>20<font class="kw">;<br></font>$px<font class="kw">=</font>120<font class="kw">;<br></font>$py<font class="kw">=</font>120<font class="kw">;<br><br></font><font class="cmt">/* */ //just vor visualisation: the straight line to mirror against<br>/* */ </font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>Line<font class="kw">(</font>$px<font class="kw">-</font>1<font class="kw">,</font>$py<font class="kw">-</font>1<font class="kw">,</font>$px<font class="kw">+</font>1<font class="kw">,</font>$py<font class="kw">+</font>1<font class="kw">);<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>Line<font class="kw">(</font>$px<font class="kw">-</font>1<font class="kw">,</font>$py<font class="kw">+</font>1<font class="kw">,</font>$px<font class="kw">+</font>1<font class="kw">,</font>$py<font class="kw">-</font>1<font class="kw">);<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>Rotate<font class="kw">(</font>$angle<font class="kw">, </font>$px<font class="kw">, </font>$py<font class="kw">);<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>Line<font class="kw">(</font>$px<font class="kw">-</font>5<font class="kw">, </font>$py<font class="kw">, </font>$px<font class="kw">+</font>60<font class="kw">, </font>$py<font class="kw">);<br></font><font class="cmt">/* */ </font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>200<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>110<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>109<font class="kw">, </font><font class="str">'MirrorL'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//Start Transformation<br></font>$pdf<font class="kw">-&gt;</font>StartTransform<font class="kw">();<br></font><font class="cmt">//mirror against the straight line<br></font>$pdf<font class="kw">-&gt;</font>MirrorL<font class="kw">(</font>$angle<font class="kw">, </font>$px<font class="kw">, </font>$py<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Rect<font class="kw">(</font>125<font class="kw">, </font>110<font class="kw">, </font>40<font class="kw">, </font>10<font class="kw">, </font><font class="str">'D'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Text<font class="kw">(</font>125<font class="kw">, </font>109<font class="kw">, </font><font class="str">'MirrorL'</font><font class="kw">);<br></font><font class="cmt">//Stop Transformation<br></font>$pdf<font class="kw">-&gt;</font>StopTransform<font class="kw">();<br><br></font>$pdf<font class="kw">-&gt;</font>Output<font class="kw">();<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>
