<H2>HTML colors</H2>
<H4 CLASS="st">Informations</H4>Author: <SCRIPT TYPE="text/javascript">document.write('<A H'+'REF="m'+'ailto:ing_ldf'+'&#'+'64;'+'yahoo.com.mx?subject=HTML%20colors">');</SCRIPT>MorphSoft<SCRIPT TYPE="text/javascript">document.write('<\/A>');</SCRIPT><BR>License: Freeware
<H4 CLASS="st">Description</H4>This script allows you to use colors using the HTML convention. You can either use the form
#RRGGBB or one of the 16 standard color names.
<H4 CLASS="st">Source</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br><font class="kw">require(</font><font class="str">'fpdf.php'</font><font class="kw">);<br><br>class </font>PDF_HTMLColor <font class="kw">extends </font>FPDF<br><font class="kw">{<br>function </font>HTML2RGB<font class="kw">(</font>$c<font class="kw">,&amp;</font>$r<font class="kw">,&amp;</font>$g<font class="kw">,&amp;</font>$b<font class="kw">)<br>{<br>&nbsp;&nbsp;&nbsp;&nbsp;static </font>$colors<font class="kw">=array(</font><font class="str">'black'</font><font class="kw">=&gt;</font><font class="str">'#000000'</font><font class="kw">,</font><font class="str">'silver'</font><font class="kw">=&gt;</font><font class="str">'#C0C0C0'</font><font class="kw">,</font><font class="str">'gray'</font><font class="kw">=&gt;</font><font class="str">'#808080'</font><font class="kw">,</font><font class="str">'white'</font><font class="kw">=&gt;</font><font class="str">'#FFFFFF'</font><font class="kw">,<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="str">'maroon'</font><font class="kw">=&gt;</font><font class="str">'#800000'</font><font class="kw">,</font><font class="str">'red'</font><font class="kw">=&gt;</font><font class="str">'#FF0000'</font><font class="kw">,</font><font class="str">'purple'</font><font class="kw">=&gt;</font><font class="str">'#800080'</font><font class="kw">,</font><font class="str">'fuchsia'</font><font class="kw">=&gt;</font><font class="str">'#FF00FF'</font><font class="kw">,<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="str">'green'</font><font class="kw">=&gt;</font><font class="str">'#008000'</font><font class="kw">,</font><font class="str">'lime'</font><font class="kw">=&gt;</font><font class="str">'#00FF00'</font><font class="kw">,</font><font class="str">'olive'</font><font class="kw">=&gt;</font><font class="str">'#808000'</font><font class="kw">,</font><font class="str">'yellow'</font><font class="kw">=&gt;</font><font class="str">'#FFFF00'</font><font class="kw">,<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="str">'navy'</font><font class="kw">=&gt;</font><font class="str">'#000080'</font><font class="kw">,</font><font class="str">'blue'</font><font class="kw">=&gt;</font><font class="str">'#0000FF'</font><font class="kw">,</font><font class="str">'teal'</font><font class="kw">=&gt;</font><font class="str">'#008080'</font><font class="kw">,</font><font class="str">'aqua'</font><font class="kw">=&gt;</font><font class="str">'#00FFFF'</font><font class="kw">);<br><br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$c<font class="kw">=</font>strtolower<font class="kw">(</font>$c<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;if(isset(</font>$colors<font class="kw">[</font>$c<font class="kw">]))<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$c<font class="kw">=</font>$colors<font class="kw">[</font>$c<font class="kw">];<br>&nbsp;&nbsp;&nbsp;&nbsp;if(</font>$c<font class="kw">{</font>0<font class="kw">}!=</font><font class="str">'#'</font><font class="kw">)<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>Error<font class="kw">(</font><font class="str">'Incorrect color: '</font><font class="kw">.</font>$c<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$r<font class="kw">=</font>hexdec<font class="kw">(</font>substr<font class="kw">(</font>$c<font class="kw">,</font>1<font class="kw">,</font>2<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$g<font class="kw">=</font>hexdec<font class="kw">(</font>substr<font class="kw">(</font>$c<font class="kw">,</font>3<font class="kw">,</font>2<font class="kw">));<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$b<font class="kw">=</font>hexdec<font class="kw">(</font>substr<font class="kw">(</font>$c<font class="kw">,</font>5<font class="kw">,</font>2<font class="kw">));<br>}<br><br>function </font>SetDrawColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">=-</font>1<font class="kw">,</font>$b<font class="kw">=-</font>1<font class="kw">)<br>{<br>&nbsp;&nbsp;&nbsp;&nbsp;if(</font>is_string<font class="kw">(</font>$r<font class="kw">))<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>HTML2RGB<font class="kw">(</font>$r<font class="kw">,</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="kw">parent::</font>SetDrawColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>}<br><br>function </font>SetFillColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">=-</font>1<font class="kw">,</font>$b<font class="kw">=-</font>1<font class="kw">)<br>{<br>&nbsp;&nbsp;&nbsp;&nbsp;if(</font>is_string<font class="kw">(</font>$r<font class="kw">))<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>HTML2RGB<font class="kw">(</font>$r<font class="kw">,</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="kw">parent::</font>SetFillColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>}<br><br>function </font>SetTextColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">=-</font>1<font class="kw">,</font>$b<font class="kw">=-</font>1<font class="kw">)<br>{<br>&nbsp;&nbsp;&nbsp;&nbsp;if(</font>is_string<font class="kw">(</font>$r<font class="kw">))<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</font>$<font class="kw">this-&gt;</font>HTML2RGB<font class="kw">(</font>$r<font class="kw">,</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;</font><font class="kw">parent::</font>SetTextColor<font class="kw">(</font>$r<font class="kw">,</font>$g<font class="kw">,</font>$b<font class="kw">);<br>}<br>}<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>

<H4 CLASS="st">Example</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br>define<font class="kw">(</font><font class="str">'FPDF_FONTPATH'</font><font class="kw">,</font><font class="str">'font/'</font><font class="kw">);<br>require(</font><font class="str">'htmlcolor.php'</font><font class="kw">);<br><br></font>$pdf <font class="kw">= new </font>PDF_HTMLColor<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font><font class="str">'blue'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetFillColor<font class="kw">(</font><font class="str">'#EE7070'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetTextColor<font class="kw">(</font><font class="str">'yellow'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetLineWidth<font class="kw">(</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">''</font><font class="kw">,</font>16<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetXY<font class="kw">(</font>80<font class="kw">,</font>40<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Cell<font class="kw">(</font>50<font class="kw">,</font>20<font class="kw">,</font><font class="str">'HTML Colors'</font><font class="kw">,</font>1<font class="kw">,</font>0<font class="kw">,</font><font class="str">'C'</font><font class="kw">,</font>1<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Output<font class="kw">();<br></font>?&gt;
</font>
</code></NOBR></TD></TR></TABLE>
