<H4 CLASS="st">Example</H4><TABLE WIDTH="100%" STYLE="color:#4040C0; border-style:ridge" BORDERCOLORLIGHT="#B0B0E0" BORDERCOLORDARK="#000000" BORDER="2" CELLPADDING=6 CELLSPACING=0 BGCOLOR="#F0F5FF"><TR><TD style="border-width:0px">
<NOBR><code><font color="#000000">
&lt;?php<br><font class="kw">require(</font><font class="str">'clipping.php'</font><font class="kw">);<br><br></font>$pdf <font class="kw">= new </font>PDF_Clipping<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br><br></font><font class="cmt">//example of clipped cell<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">''</font><font class="kw">,</font>14<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetX<font class="kw">(</font>72<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>ClippedCell<font class="kw">(</font>60<font class="kw">,</font>6<font class="kw">,</font><font class="str">'These are clipping examples'</font><font class="kw">,</font>1<font class="kw">);<br><br></font><font class="cmt">//example of clipping text<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">'B'</font><font class="kw">,</font>120<font class="kw">);<br></font><font class="cmt">//set the outline color<br></font>$pdf<font class="kw">-&gt;</font>SetDrawColor<font class="kw">(</font>0<font class="kw">);<br></font><font class="cmt">//set the outline width (note that only its outer half will be shown)<br></font>$pdf<font class="kw">-&gt;</font>SetLineWidth<font class="kw">(</font>2<font class="kw">);<br></font><font class="cmt">//draw the clipping text<br></font>$pdf<font class="kw">-&gt;</font>ClippingText<font class="kw">(</font>40<font class="kw">,</font>55<font class="kw">,</font><font class="str">'CLIPS'</font><font class="kw">,</font>true<font class="kw">);<br></font><font class="cmt">//fill it with the image<br></font>$pdf<font class="kw">-&gt;</font>Image<font class="kw">(</font><font class="str">'clips.jpg'</font><font class="kw">,</font>40<font class="kw">,</font>10<font class="kw">,</font>130<font class="kw">);<br></font><font class="cmt">//remove the clipping<br></font>$pdf<font class="kw">-&gt;</font>UnsetClipping<font class="kw">();<br><br></font><font class="cmt">//example of clipping rectangle<br></font>$pdf<font class="kw">-&gt;</font>ClippingRect<font class="kw">(</font>45<font class="kw">,</font>65<font class="kw">,</font>116<font class="kw">,</font>20<font class="kw">,</font>true<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Image<font class="kw">(</font><font class="str">'clips.jpg'</font><font class="kw">,</font>40<font class="kw">,</font>10<font class="kw">,</font>130<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>UnsetClipping<font class="kw">(); <br><br></font><font class="cmt">//example of clipping ellipse<br></font>$pdf<font class="kw">-&gt;</font>ClippingEllipse<font class="kw">(</font>61<font class="kw">,</font>104<font class="kw">,</font>16<font class="kw">,</font>10<font class="kw">,</font>true<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Image<font class="kw">(</font><font class="str">'clips.jpg'</font><font class="kw">,</font>40<font class="kw">,</font>10<font class="kw">,</font>130<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>UnsetClipping<font class="kw">(); <br><br></font><font class="cmt">//example of clipping circle<br></font>$pdf<font class="kw">-&gt;</font>ClippingCircle<font class="kw">(</font>103<font class="kw">,</font>104<font class="kw">,</font>10<font class="kw">,</font>true<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Image<font class="kw">(</font><font class="str">'clips.jpg'</font><font class="kw">,</font>40<font class="kw">,</font>10<font class="kw">,</font>130<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>UnsetClipping<font class="kw">();<br><br></font><font class="cmt">//example of clipping polygon<br></font>$pdf<font class="kw">-&gt;</font>ClippingPolygon<font class="kw">(array(</font>145<font class="kw">,</font>94<font class="kw">,</font