</font>$<font class="kw">this-&gt;</font>Cell<font class="kw">(</font>0<font class="kw">,</font>3<font class="kw">,</font><font class="str">'Page '</font><font class="kw">.</font>$<font class="kw">this-&gt;</font>PageNo<font class="kw">().</font><font class="str">'/{nb}'</font><font class="kw">,</font>0<font class="kw">,</font>0<font class="kw">,</font><font class="str">'C'</font><font class="kw">);<br>&nbsp;&nbsp;&nbsp;&nbsp;}<br>}<br><br></font>$pdf <font class="kw">= new </font>PDF<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>AliasNbPages<font class="kw">();<br></font>$pdf<font class="kw">-&gt;</font>SetDoubleSided<font class="kw">(</font>20<font class="kw">,</font>10<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetFont<font class="kw">(</font><font class="str">'Arial'</font><font class="kw">,</font><font class="str">''</font><font class="kw">,</font>12<font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>AddPage<font class="kw">();<br>for (</font>$i<font class="kw">=</font>0<font class="kw">; </font>$i <font class="kw">&lt; </font>60<font class="kw">; </font>$i<font class="kw">++ )<br>&nbsp;&nbsp;&nbsp;&nbsp;</font>$pdf<font class="kw">-&gt;</font>MultiCell<font class="kw">(</font>0<font class="kw">, </font>10<font class="kw">, </font>str_repeat<font class="kw">(</font><font class="str">'a lot of text '</font><font class="kw">,</font>30<font class="kw">).</font><font class="str">"...\n"</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>SetDisplayMode<font class="kw">(</font><font class="str">'fullpage'</font><font class="kw">, </font><font class="str">'single'</font><font class="kw">);<br></font>$pdf<font class="kw">-&gt;</font>Output<font class="kw">();<br></font>?&gt;
